(59, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-03-17 00:32:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'amazon_pay_reactivate_q1-24', 'marketing', 'en_US', 'Special offer: Activate 0% promo rate for Amazon Pay', 'For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in the United Kingdom. Terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2024-03-17 00:32:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-03-17 00:32:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc_admin_UKpricingchange_WCPay_Q1_2024', 'marketing', 'en_US', 'Fee Changes for WooPayments in the EEA and UK', 'WooPayments fees for transactions between the EEA and UK will change as of 1st March, 2024. Transactions between the UK and EEA are now deemed by card networks to be cross-border, thus international payment fees will be applicable. This also changes base processing fees.', '{}', 'pending', 'woocommerce.com', '2024-03-17 00:32:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'airwallex_q1_2024', 'marketing', 'en_US', 'Save big on foreign exchange (FX) fees', 'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more  without the extra FX fees. Simple, cost-effective, fast.', '{}', 'unactioned', 'woocommerce.com', '2024-03-17 00:33:38', NULL, 0, 'plain', '', 0, 1, 'info'),
(64, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to Woo.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-17 00:32:07', NULL, 0, 'plain', '', 0, 1, 'info'),
(65, 'wc-payments-notes-set-https-for-checkout', 'info', 'en_US', 'Enable secure checkout', 'Enable HTTPS on your checkout pages to display all available payment methods and protect your customers data.', '{}', 'unactioned', 'woocommerce-payments', '2024-03-17 00:33:41', NULL, 0, 'plain', '', 0, 1, 'info'),
(66, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-18 17:32:43', NULL, 0, 'plain', '', 0, 1, 'info'),
(67, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-18 17:32:43', NULL, 0, 'plain', '', 0, 1, 'info'),
(68, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-18 17:32:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2024-04-01 18:07:50', NULL, 0, 'plain', '', 0, 0, 'info');
